select * from t_user_company where 1=1
and code=#{userCompany.code,jdbcType=VARCHAR}
and company_name like '%${userCompany.companyName}%'
and (wx_contract_phone1=#{userCompany.phone,jdbcType=VARCHAR}
    or wx_contract_phone2=#{userCompany.phone,jdbcType=VARCHAR})

limit #{start,jdbcType=INTEGER} , #{limit,jdbcType=INTEGER}

SELECT * FROM (SELECT A.*, ROWNUM RN FROM (SELECT * FROM t_user_company WHERE 1=1
and code=#{userCompany.code,jdbcType=VARCHAR}
and company_name like '%${userCompany.companyName}%'
and (wx_contract_phone1=#{userCompany.phone,jdbcType=VARCHAR}
    or wx_contract_phone2=#{userCompany.phone,jdbcType=VARCHAR})) A WHERE
ROWNUM <= ${start} + ${limit})WHERE RN > #{start,jdbcType=INTEGER}
